// }

// store.dispatch(changeNickname("mighty tak"));

//(이전상태,액션을 통해서) => 다음상태를 만드는거
const rootReducer = combineReducers({
  index: (state = {}, action) => {
    switch (action.type) {
      case HYDRATE:
        console.log("HYDRATE", action);
        return { ...state, ...action.payload };
      default:
        return state;
    }
  },
  user,
  post,
});

export default rootReducer;
